import 'package:flutter/material.dart';import 'package:projeto_flutter_21805677/data/cm_database.dart';import 'package:projeto_flutter_21805677/pages/navigation.dart';import 'package:projeto_flutter_21805677/repository/parks_repository.dart';import 'package:projeto_flutter_21805677/services/connectivity_service.dart';import 'package:projeto_flutter_21805677/services/emel_service.dart';import 'package:provider/provider.dart';import 'package:projeto_flutter_21805677/http/http_client.dart';void main() {  final cmDatabase = CmDatabase();  final emelService = Emelservice(client: HttpClient());  runApp(MultiProvider(    providers: [      ChangeNotifierProvider(create: (BuildContext context) => NavigationViewModel()),      Provider<CmDatabase>(create: (_) => cmDatabase),      Provider<Emelservice>(create: (_) => emelService),      Provider<ParksRepository>(          create: (_) => ParksRepository(              local: cmDatabase,              remote: emelService,              connectivityService: ConnectivityService()          )),    ],      child: const MyApp()));}class MyApp extends StatelessWidget {  const MyApp({super.key});  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    var colorScheme = ColorScheme.fromSeed(seedColor: Colors.blue);    final cmDatabase = context.read<CmDatabase>();    return FutureBuilder(        future: cmDatabase.init(),        builder: (_,snapshot) {          if(snapshot.connectionState == ConnectionState.done){            return MaterialApp(              title: 'Projeto CM',              theme: ThemeData(                  colorScheme: colorScheme,                  useMaterial3: true,                  appBarTheme: ThemeData.from(                      colorScheme: colorScheme).appBarTheme.copyWith(backgroundColor: colorScheme.primary, foregroundColor: colorScheme.surface,                  )              ),              home: Navigation(),            );          } else {            return MaterialApp(              home: Center(                child: CircularProgressIndicator(),              ),            );          }        });  }}